alfs.package:


        &packages_dir;/&gpm-package;
        &build_dir;


    &bootscripts-get;


      &build_dir;/&gpm-directory;


      -N
      -p1
      -i &packages_dir;/&gpm-segfault-patch;


      -N
      -p1
      -i &packages_dir;/&gpm-silent-patch;


      LDFLAGS='-lm'
      --prefix=/usr
      --sysconfdir=/etc



      install


      force
      gpm-root.conf
      /etc

    &ldconfig;



        &build_dir;/&blfs-bootscripts-directory;


        install-gpm


        mouse

	  =# Begin /etc/sysconfig/mouse
      =
	  =MDEVICE="&mouse_device;"
	  =PROTOCOL="&mouse_protocol;"
      =GPMOPTS="&mouse_options;"
      =
	  =# End /etc/sysconfig/mouse



    &bootscripts-clean;

      &build_dir;/&gpm-directory;
