alfs.package:


        &packages_dir;/&dhclient-package;
        &build_dir;


    &bootscripts-get;


        &build_dir;/&dhclient-directory;




        LIBDIR=/usr/lib
        INCDIR=/usr/include
        install




        &build_dir;/&blfs-bootscripts-directory;


        install-service-dhclient




        /etc/sysconfig/network-devices/ifconfig.eth0/dhclient

	  =ONBOOT=yes
	  =SERVICE=dhclient"
	  =DHCP_START="&dhclient-start-params;"
	  =DHCP_STOP="&dhclient-stop-params;"



        /etc/dhclient.conf

	  =# dhclient.conf
	  =
	  =interface "eth0" {
	  = prepend domain-name-servers 127.0.0.1;
	  = request subnet-mask, broadcast-address, time-offset, routers,
	  =	    domain-name, domain-name-servers, host-name; 
	  = require subnet-mask, domain-name-servers;
	  =}
	  =# end dhclient.conf



    &bootscripts-clean;

      &build_dir;/&dhclient-directory;
