alfs.package:


        &packages_dir;/&dhcpcd-package;
        &build_dir;


    &bootscripts-get;


        &build_dir;/&dhcpcd-directory;


        -N
        -p1
        -i &packages_dir;/&dhcpcd-patch;




        &build_dir;/&dhcpcd-directory;


        --prefix=""
        --sysconfdir=/var/lib
        --mandir=/usr/share/man



        install




        &build_dir;/&blfs-bootscripts-directory;


        install-service-dhcpcd




        /etc/sysconfig/network-devices/ifconfig.eth0/dhcpcd

	  =ONBOOT=yes
	  =SERVICE=dhcpcd
	  =DHCP_START="&dhcpcd-start-params;"
	  =DHCP_STOP="&dhcpcd-stop-params;"
	  =# Set PRINTIP="yes" to have the script print the DHCP assigned IP address.
	  =PRINTIP="no"

	  =# Set PRINTALL="yes" to print the DHCP assigned values for IP, SM, DG, and 1st NS.
	  =# This requires PRINTIP="yes"
	  =PRINTALL="no"



    &bootscripts-clean;

      &build_dir;/&dhcpcd-directory;
