alfs.package:


        &packages_dir;/&dhcp-package;
        &build_dir;


    &bootscripts-get;


	&build_dir;/&dhcp-directory;


	-N
	-p1
	-i &packages_dir;/&dhcp-iproute2-patch;




        &build_dir;/&dhcp-directory;




        LIBDIR=/usr/lib
        INCDIR=/usr/include
        install


        /var/state/dhcp/dhcpd.leases


